// ============================================
// Selectionner un theme et verifier les doublons
// ============================================

const SOLUTIONS = [
  { slug: 'strategie-ia', label: 'Strategie IA & Agents Autonomes', color: 'purple', icon: 'fa-brain' },
  { slug: 'automation-n8n', label: 'Automatisation des Processus (n8n)', color: 'blue', icon: 'fa-robot' },
  { slug: 'audit-performance-it', label: 'Audit & Performance IT', color: 'green', icon: 'fa-clipboard-check' },
  { slug: 'dsi-externalise', label: 'DSI Externalise & Gouvernance IT', color: 'indigo', icon: 'fa-building' },
  { slug: 'data-erp', label: 'Data, ERP & Systemes d Information', color: 'cyan', icon: 'fa-database' },
  { slug: 'cybersecurite-infrastructure', label: 'Cybersecurite & Infrastructure Cloud', color: 'red', icon: 'fa-shield-halved' },
  { slug: 'strategie-marketing-branding', label: 'Strategie Marketing & Branding', color: 'pink', icon: 'fa-bullhorn' },
  { slug: 'seo-acquisition-digitale', label: 'SEO & Acquisition Digitale', color: 'rose', icon: 'fa-search' },
  { slug: 'geo-referencement-ia', label: 'GEO Referencement & IA Generative', color: 'orange', icon: 'fa-globe' },
  { slug: 'google-business-profile', label: 'Google Business Profile & SEO Local', color: 'yellow', icon: 'fa-map-marker-alt' },
  { slug: 'publicite-digitale', label: 'Publicite Digitale (Google Ads, Meta Ads)', color: 'amber', icon: 'fa-ad' }
];

// Recuperer les articles deja publies depuis Google Sheets
let publishedArticles = [];
try {
  const items = $input.all();
  publishedArticles = items
    .map(item => ({
      slug: item.json.slug || '',
      title: item.json.title || '',
      tag: item.json.tag || ''
    }))
    .filter(a => a.slug);
} catch(e) {
  publishedArticles = [];
}
const publishedSlugs = publishedArticles.map(a => a.slug);
const publishedTitles = publishedArticles.map(a => a.title);

// Rotation hebdo sur les 11 solutions
const weekNumber = Math.floor((Date.now() - new Date('2026-01-01').getTime()) / (7 * 24 * 60 * 60 * 1000));
const themeIndex = weekNumber % SOLUTIONS.length;
const theme = SOLUTIONS[themeIndex];

const today = new Date().toISOString().split('T')[0];
const todayFr = new Date().toLocaleDateString('fr-FR', { day: 'numeric', month: 'long', year: 'numeric' });

return [{
  json: {
    theme,
    allSolutions: SOLUTIONS,
    publishedSlugs,
    publishedTitles,
    publishedCount: publishedArticles.length,
    date: today,
    dateFr: todayFr
  }
}];

// Extract Unsplash image, fallback to profile pic
const input = $input.first().json;
const parsedData = $('Parse Agent Output').first().json;

let imageUrl = 'https://nathanibgui.com/assets/images/nathanibguiProfile.png';
let imageAlt = parsedData.topic.title;
let imageCredit = '';

try {
  if (input.urls && input.urls.regular) {
    imageUrl = input.urls.regular;
    imageAlt = input.alt_description || parsedData.topic.title;
    imageCredit = 'Photo by ' + input.user.name + ' on Unsplash';
  }
} catch(e) {}

return [{
  json: {
    ...parsedData,
    image: { url: imageUrl, alt: imageAlt, credit: imageCredit }
  }
}];

