// Update articles.json - handle n8n array item splitting
const buildData = $('Assembler HTML').first().json;
const newEntry = buildData.articlesJsonEntry;

let articles = [];
try {
  const items = $input.all();
  const first = items[0]?.json;

  if (Array.isArray(first)) {
    // Response returned as single array
    articles = first;
  } else if (first && first.slug) {
    // n8n split the array into individual items
    articles = items.map(i => i.json);
  } else if (first && typeof first === 'object') {
    // Try to find array in response body
    const keys = Object.keys(first);
    for (const k of keys) {
      if (Array.isArray(first[k])) { articles = first[k]; break; }
    }
  }
} catch(e) {
  articles = [];
}

// Remove existing entry with same slug
articles = articles.filter(a => a.slug !== newEntry.slug);

// Prepend new article (newest first)
articles.unshift(newEntry);

// Convert to binary for SFTP upload
const jsonStr = JSON.stringify(articles, null, 2);
const buffer = Buffer.from(jsonStr, 'utf-8');

return [{
  json: { articlesCount: articles.length },
  binary: {
    data: {
      data: buffer.toString('base64'),
      mimeType: 'application/json',
      fileName: 'articles.json',
      fileExtension: 'json'
    }
  }
}];

// Parse la sortie de l'Agent IA et merge avec les donnees du topic
const input = $input.first().json;
const topicData = $('Selectionner Sujet').first().json;

// Extraire le JSON de la reponse de l'agent
let responseText = '';
if (typeof input.output === 'string') {
  responseText = input.output;
} else if (input.message && input.message.content) {
  responseText = input.message.content;
} else if (typeof input.text === 'string') {
  responseText = input.text;
} else {
  responseText = JSON.stringify(input);
}

// Nettoyer les backticks markdown si presentes
responseText = responseText.replace(/^```json\s*/, '').replace(/\s*```$/, '').trim();

let article;
try {
  article = JSON.parse(responseText);
} catch(e) {
  // Tenter d'extraire le JSON d'un texte mixte
  const match = responseText.match(/\{[\s\S]*\}/);
  if (match) {
    article = JSON.parse(match[0]);
  } else {
    throw new Error('Impossible de parser la reponse de l agent: ' + responseText.substring(0, 200));
  }
}

// Valider les champs obligatoires
const required = ['title', 'slug', 'metaDescription', 'sections', 'introduction', 'faq'];
for (const field of required) {
  if (!article[field]) {
    throw new Error('Champ manquant dans la reponse: ' + field);
  }
}

// Utiliser la couleur du theme si l'agent n'en a pas fourni
const color = article.color || topicData.theme.color;
const icon = article.icon || topicData.theme.icon;

const date = topicData.date;
const dateFr = topicData.dateFr;
const slug = article.slug;
const canonicalUrl = 'https://nathanibgui.com/blog/' + slug;
const fileName = slug + '.html';

return [{
  json: {
    article,
    topic: {
      title: article.title,
      titleMain: article.titleMain || article.title,
      titleHighlight: article.titleHighlight || '',
      slug,
      keywords: article.keywords || '',
      color,
      icon,
      tag: article.tag || topicData.theme.label,
      readingTime: article.readingTime || 10,
      unsplashQuery: article.unsplashQuery || 'technology'
    },
    allSolutions: topicData.allSolutions,
    date, dateFr, canonicalUrl, fileName
  }
}];
