  { slug: 'publicite-digitale', label: 'Publicité Digitale', color: 'amber', icon: 'fa-ad' }
];

const TOPIC_IDEAS = [
  {
    title: 'Automatiser son entreprise avec n8n : guide pratique pour PME',
    slug: 'automatiser-entreprise-n8n-guide-pme',
    keywords: 'automatisation PME, n8n tutoriel, workflow automation, automatiser processus entreprise',
    mainSolution: 'automation-n8n',
    relatedSolutions: ['strategie-ia', 'dsi-externalise', 'data-erp'],
    color: 'blue', icon: 'fa-robot', readingTime: 10, tag: 'Automation',
    unsplashQuery: 'automation technology office'
  },
  {
    title: 'Cybersécurité PME : les 10 menaces de 2026 et comment s\'en protéger',
    slug: 'cybersecurite-pme-menaces-2026',
    keywords: 'cybersécurité PME, menaces informatiques 2026, protection données entreprise',
    mainSolution: 'cybersecurite-infrastructure',
    relatedSolutions: ['audit-performance-it', 'dsi-externalise', 'automation-n8n'],
    color: 'red', icon: 'fa-shield-halved', readingTime: 8, tag: 'Cybersécurité',
    unsplashQuery: 'cybersecurity digital lock'
  },
  {
    title: 'DSI externalisé vs DSI interne : quel modèle pour votre PME ?',
    slug: 'dsi-externalise-vs-interne-pme',
    keywords: 'DSI externalisé, direction informatique PME, CIO as a service',
    mainSolution: 'dsi-externalise',
    relatedSolutions: ['audit-performance-it', 'strategie-ia', 'cybersecurite-infrastructure'],
    color: 'indigo', icon: 'fa-building', readingTime: 9, tag: 'Gouvernance IT',
    unsplashQuery: 'CTO executive business strategy'
  },
  {
    title: 'ERP pour PME : comment choisir et réussir son déploiement en 2026',
    slug: 'erp-pme-choix-deploiement-2026',
    keywords: 'ERP PME, choisir ERP, déploiement ERP, gestion données entreprise',
    mainSolution: 'data-erp',
    relatedSolutions: ['automation-n8n', 'audit-performance-it', 'dsi-externalise'],
    color: 'cyan', icon: 'fa-database', readingTime: 11, tag: 'Data & ERP',
    unsplashQuery: 'data analytics dashboard business'
  },
  {
    title: 'Google Business Profile : optimiser sa fiche pour dominer le local en 2026',
    slug: 'google-business-profile-optimisation-2026',
    keywords: 'Google Business Profile, fiche Google, référencement local, SEO local PME',
    mainSolution: 'google-business-profile',
    relatedSolutions: ['seo-acquisition-digitale', 'geo-referencement-ia', 'strategie-marketing-branding'],
    color: 'yellow', icon: 'fa-map-marker-alt', readingTime: 7, tag: 'SEO Local',
    unsplashQuery: 'local business storefront google maps'
  },
  {
    title: 'Marketing digital PME : construire une stratégie qui convertit',
    slug: 'marketing-digital-pme-strategie-conversion',
    keywords: 'marketing digital PME, stratégie marketing, branding entreprise',
    mainSolution: 'strategie-marketing-branding',
    relatedSolutions: ['seo-acquisition-digitale', 'publicite-digitale', 'geo-referencement-ia'],
    color: 'pink', icon: 'fa-bullhorn', readingTime: 9, tag: 'Marketing',
    unsplashQuery: 'digital marketing strategy team'
  },
  {
    title: 'Google Ads et Meta Ads pour PME : maximiser le ROI de vos campagnes',
    slug: 'google-ads-meta-ads-pme-roi-2026',
    keywords: 'Google Ads PME, Meta Ads, publicité digitale, ROI campagnes publicitaires',
    mainSolution: 'publicite-digitale',
    relatedSolutions: ['strategie-marketing-branding', 'seo-acquisition-digitale', 'automation-n8n'],
    color: 'amber', icon: 'fa-ad', readingTime: 8, tag: 'Publicité',
    unsplashQuery: 'online advertising campaign ROI'
  },
  {
    title: 'Agents IA autonomes : transformer vos processus métier en 2026',
    slug: 'agents-ia-autonomes-processus-metier-2026',
    keywords: 'agents IA, IA autonome entreprise, automatisation intelligente',
    mainSolution: 'strategie-ia',
    relatedSolutions: ['automation-n8n', 'data-erp', 'dsi-externalise'],
    color: 'purple', icon: 'fa-brain', readingTime: 10, tag: 'IA & Agents',
    unsplashQuery: 'artificial intelligence robot technology'
  }
];

const weekNumber = Math.floor((Date.now() - new Date('2026-01-01').getTime()) / (7 * 24 * 60 * 60 * 1000));
const topicIndex = weekNumber % TOPIC_IDEAS.length;
const topic = TOPIC_IDEAS[topicIndex];
const relatedData = topic.relatedSolutions.map(slug => SOLUTIONS.find(s => s.slug === slug)).filter(Boolean);
const today = new Date().toISOString().split('T')[0];
const todayFr = new Date().toLocaleDateString('fr-FR', { day: 'numeric', month: 'long', year: 'numeric' });

return [{
  json: {
    topic, relatedSolutions: relatedData, allSolutions: SOLUTIONS,
    date: today, dateFr: todayFr,
    fileName: topic.slug + '.html',
    canonicalUrl: 'https://nathanibgui.com/blog/' + topic.slug
  }
}];

// Extract Unsplash image data, fallback to default if API fails
const input = $input.first().json;
const topicData = $('Selectionner Sujet').first().json;

let imageUrl = 'https://nathanibgui.com/assets/images/nathanibguiProfile.png';
let imageAlt = topicData.topic.title;
let imageCredit = '';

try {
  if (input.results && input.results.length > 0) {
    const photo = input.results[0];
    imageUrl = photo.urls.regular;
    imageAlt = photo.alt_description || topicData.topic.title;
    imageCredit = `Photo by ${photo.user.name} on Unsplash`;
  }
} catch(e) {
  // Keep defaults
}

return [{
  json: {
    ...topicData,
    image: { url: imageUrl, alt: imageAlt, credit: imageCredit }
  }
}];

