// Parse AI response and build the full HTML page
const input = $input.first().json;
const topicData = $('Traiter Image').first().json;
const topic = topicData.topic;
const date = topicData.date;
const dateFr = topicData.dateFr;
const canonicalUrl = topicData.canonicalUrl;
const fileName = topicData.fileName;
const image = topicData.image;

// Extract JSON from AI response (handle both OpenAI and Anthropic formats)
let responseText = '';
if (input.message && input.message.content) {
  responseText = input.message.content; // OpenAI format
} else if (input.content && input.content[0] && input.content[0].text) {
  responseText = input.content[0].text; // Anthropic format
} else if (typeof input.text === 'string') {
  responseText = input.text;
} else {
  responseText = JSON.stringify(input);
}

responseText = responseText.replace(/^```json\s*/, '').replace(/\s*```$/, '').trim();
const article = JSON.parse(responseText);

// Build FAQ JSON-LD
const faqJsonLd = {
  "@context": "https://schema.org",
  "@type": "FAQPage",
  "mainEntity": article.faq.map(f => ({
    "@type": "Question",
    "name": f.question,
    "acceptedAnswer": { "@type": "Answer", "text": f.answer }
  }))
};

const articleTagsMeta = article.articleTags.map(t =>
  `    <meta property="article:tag" content="${t}">`
).join('\n');

const sectionsHtml = article.sections.map(section => `
        <section class="glass p-8 sm:p-10 rounded-3xl border border-white/5 mb-8">
            <h2 class="text-2xl sm:text-3xl font-black text-white mb-6">${section.title}</h2>
            <div class="text-slate-300 leading-relaxed space-y-4">
                ${section.content}
            </div>
        </section>`).join('\n');

const faqHtml = article.faq.map(f => `
            <div class="glass p-6 rounded-2xl border border-white/5">
                <h3 class="font-bold text-white mb-3">${f.question}</h3>
                <p class="text-sm text-slate-400 leading-relaxed">${f.answer}</p>
            </div>`).join('\n');

const colorMap = {
  purple: { radial: 'rgba(147, 51, 234, 0.15)', gradient: 'from-purple-400 to-purple-600' },
  blue: { radial: 'rgba(59, 130, 246, 0.15)', gradient: 'from-blue-400 to-blue-600' },
  green: { radial: 'rgba(34, 197, 94, 0.15)', gradient: 'from-green-400 to-green-600' },
  red: { radial: 'rgba(239, 68, 68, 0.15)', gradient: 'from-red-400 to-red-600' },
  indigo: { radial: 'rgba(99, 102, 241, 0.15)', gradient: 'from-indigo-400 to-indigo-600' },
  cyan: { radial: 'rgba(6, 182, 212, 0.15)', gradient: 'from-cyan-400 to-cyan-600' },
  pink: { radial: 'rgba(236, 72, 153, 0.15)', gradient: 'from-pink-400 to-pink-600' },
  rose: { radial: 'rgba(244, 63, 94, 0.15)', gradient: 'from-rose-400 to-rose-600' },
  orange: { radial: 'rgba(249, 115, 22, 0.15)', gradient: 'from-orange-400 to-orange-600' },
  yellow: { radial: 'rgba(234, 179, 8, 0.15)', gradient: 'from-yellow-400 to-yellow-600' },
  amber: { radial: 'rgba(245, 158, 11, 0.15)', gradient: 'from-amber-400 to-amber-600' }
};
const colors = colorMap[topic.color] || colorMap.blue;

// Hero image HTML
const heroImageHtml = image.url !== 'https://nathanibgui.com/assets/images/nathanibguiProfile.png'
  ? `\n        <!-- HERO IMAGE -->\n        <div class="max-w-4xl mx-auto px-4 sm:px-8 mb-12">\n            <div class="rounded-3xl overflow-hidden border border-white/5">\n                <img src="${image.url}" alt="${image.alt}" class="w-full h-64 sm:h-80 md:h-96 object-cover" loading="lazy">\n            </div>\n            ${image.credit ? '<p class="text-[10px] text-slate-600 mt-2 text-center">' + image.credit + '</p>' : ''}\n        </div>`
  : '';

const html = `<!DOCTYPE html>
